#include<bits/stdc++.h>
#define loop(i,l,r) for(register int i=(l),_##i##_=(r);i<=_##i##_;++i)
#define MAXN 100005
using namespace std;
struct BIT{
	#define lowbit(x) ((x)&-(x))
	#define T int
	T t[MAXN];
	int N;
	BIT(const int& n){
		N=n;
		memset(t,0,sizeof t);
	}
	inline void add(const int &x,const T &k){
		for(register int i=x;i<=N;i+=lowbit(i))t[i]+=k;
	}
	inline T query(const int &x){
		register T sum=0;
		for(register int i=x;i;i-=lowbit(i))sum+=t[i];
		return sum;
	}
};
