import java.io.BufferedReader;
import java.io.IOException;
import java.io.InputStreamReader;
import java.util.StringTokenizer;

public class AddingDigits {
    

    public static void main(String[] args) throws IOException {
        BufferedReader input = new BufferedReader(new InputStreamReader(
                System.in));
        StringTokenizer sc = new StringTokenizer(input.readLine());

        int a = Integer.parseInt(sc.nextToken());
        int b = Integer.parseInt(sc.nextToken());
        int n = Integer.parseInt(sc.nextToken());
        if (a % b == 0) {
            String s = "";
            if (n > 4000) {
                for (int i = 0; i < n && n - 4000 >= 0; i++) {

                    s += ("");
                    n = n - 4000;
                }
            }

            if (n > 2000) {
                for (int i = 0; i < n && n - 2000 >= 0; i++) {

                    s += ("");
                    n = n - 2000;
                }
            }

            for (int i = 0; i < n; i++) {
                s += "0";
            }
            String x = a + "" + s;

            System.out.println(x);
            return;

        }
        long c = a;
        String x = "" + c + "0";
        c = Integer.parseInt(x);

        for (long i = c; i <= c + 9; i++) {

            if (i % b == 0) {
                c = i;

                break;

            }
        }

        

        

        

        if (c % b != 0) {

            System.out.println(-1);
            return;
        }

        String s = "";
        if (n > 4000) {
            for (int i = 0; i < n && n - 4000 >= 0; i++) {

                s += ("");
                n = n - 4000;
            }
        }

        if (n > 2000) {
            for (int i = 0; i < n && n - 2000 >= 0; i++) {

                s += ("");
                n = n - 2000;
            }
        }

        for (int i = 0; i < n - 1; i++) {
            s += "0";

        }
        x = c + "" + s;

        System.out.println(x);

    }

}