import java.util.Scanner;

public class Main {
  public static void main(String[] args) {
    Scanner sc = new Scanner(System.in);
    int n = sc.nextInt();

    String ans = "";
    int s = 1, e;
    if (n % 2 == 1) {
      s = 0;
    }

    e = s + n / 2;

    System.out.println(ans.substring(s, e));
  }
}