import java.io.BufferedReader;
import java.io.IOException;
import java.io.InputStreamReader;
import java.util.StringTokenizer;

public class id0 {
    public static void main(String[] args) throws IOException {
        reader input = new reader();
        int t=input.nextInt();
        String nine="";
        String eight="";
        for(int i=0;i<t;i++) {
            int n=input.nextInt();
            int x=(int)Math.ceil((double)n/4);
            int a=(n-x)/10000;
            String ans="";
            for(int j=0;j<a;j++){
                ans+=nine;
            }
            ans+=nine.substring(0,(n-x)%10000);
            int b=x/10000;
            for(int j=0;j<b;j++){
                ans+=eight;
            }
            ans+=eight.substring(0,x%10000);
            System.out.println(ans);
        }
    }
}

class reader {
    BufferedReader br;
    StringTokenizer st;

    public reader()
    {
        br = new BufferedReader(new
                InputStreamReader(System.in));
    }
    String next()
    {
        while (st == null || !st.hasMoreElements())
        {
            try
            {
                st = new StringTokenizer(br.readLine());
            }
            catch (IOException  e)
            {
                e.printStackTrace();
            }
        }
        return st.nextToken();
    }
    int nextInt()
    {
        return Integer.parseInt(next());
    }
    long nextLong()
    {
        return Long.parseLong(next());
    }
    double nextDouble()
    {
        return Double.parseDouble(next());
    }
    String nextLine()
    {
        String str = "";
        try
        {
            str = br.readLine();
        }
        catch (IOException e)
        {
            e.printStackTrace();
        }
        return str;
    }
}
