int intersect(int p1, int k1, int p2, int k2) {
    if (k1 < p2 || k2 < p1)
        return 0;
    return (k1 < k2 ? k1 : k2) - (p1 > p2 ? p1 : p2) + 1;
}

int main() {
    int pl, pr, vl, vr, k;
    scanf("%d%d%d%d%d", &pl, &pr, &vl, &vr, &k);
    int wsk = 1;
    LL res = 0;
    while (wsk + k < MR) {
        res += intersect(t[wsk - 1] + 1, t[wsk], pl, pr) * (LL) intersect(t[wsk + k - 1], t[wsk + k] - 1, vl, vr);
        res += intersect(t[wsk - 1] + 1, t[wsk], vl, vr) * (LL) intersect(t[wsk + k - 1], t[wsk + k] - 1, pl, pr);
        if (k == 1)
            res -= intersect(t[wsk], t[wsk], pl, pr) * intersect(t[wsk], t[wsk], vl, vr);
        wsk++;
    }
    printf("%.10lf\n", res / (double)((pr - pl + 1) * (LL)(vr - vl + 1)));
    return 0;
}
